mapper.insert:
INSERT
INTO
    TJ_QUESTION(
        MEMBER_ID
    ,   QUESTION_TITLE
    ,   QUESTION_CONTENT
    )
VALUES (
    #{ memberId }
,   #{ questionTitle }
,   #{ questionContent }
)

INSERT
INTO
    TJ_ANSWER(
        MEMBER_ID
    ,   REF_BOARD_NO
    ,   ANSWER_CONTENT
    )
VALUES (
    #{ memberId } 
,   #{ refBoardNo }
,   #{ answerContent }
)

mapper.update:
UPDATE TJ_QUESTION
SET VIEW_COUNT = VIEW_COUNT + 1
WHERE QUESTION_NO = #{questionNo}

mapper.select:

FROM TJ_QUESTION Q
JOIN TJ_MEMBER M ON Q.MEMBER_ID = M.MEMBER_ID
WHERE IS_DELETED = 'N' AND Q.QUESTION_NO = #{questionNo}
